import React, { useState } from "react";
import s from "./modal.module.scss";
import sgMail from "@sendgrid/mail";

const API_KEY =
  "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";
sgMail.setApiKey(API_KEY);

export const ModalZap = ({ closeModal }) => {
  const [email, setEmail] = useState("");
  const [telefon, setTelefon] = useState("");
  const [trescZapytania, setTrescZapytania] = useState("");

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    const message = {
      to: "mikolaj.lesniczuk@gmail.com",
      from: email,
      subject: "Nowe zapytanie",
      text: `Telefon: ${telefon}\nTreść zapytania: ${trescZapytania}`,
    };

    try {
      await sgMail.send(message);
      console.log("E-mail wysłany");
    } catch (error) {
      console.error("Błąd podczas wysyłania e-maila:", error);
    }
  };

  return (
    <div className={s.bg}>
      <div className={s.bgmodal}>
        <p className={s.close} onClick={closeModal}>
          X
        </p>
        <form onSubmit={handleFormSubmit}>
          <div className={s.flex}>
            <label className={s.title}>Email</label>
            <input
              type="email"
              placeholder="Twój email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className={s.flex}>
            <label className={s.title}>Telefon</label>
            <input
              type="tel"
              placeholder="Twój telefon"
              value={telefon}
              onChange={(e) => setTelefon(e.target.value)}
            />
          </div>
          <div className={s.flex}>
            <label className={s.title}>Treść zapytania</label>
            <input
              className={s.text}
              type="text"
              placeholder="Treść zapytania"
              value={trescZapytania}
              onChange={(e) => setTrescZapytania(e.target.value)}
            />
            <button type="submit">Wyślij formularz</button>
          </div>
        </form>
      </div>
    </div>
  );
};
