import React, { useState } from "react";
import s from "./questions.module.css";
import arrow from "../../images/svg/icon-arrow.svg";

const Question = ({ question, description }) => {
  const [isDescriptionVisible, setIsDescriptionVisible] = useState(false);

  const toggleDescription = () => {
    setIsDescriptionVisible(!isDescriptionVisible);
  };

  return (
    <div className={s.flex}>
      <p>{question}</p>
      <img
        onClick={toggleDescription}
        className={s.arrow}
        src={arrow}
        alt="img"
      ></img>
      {isDescriptionVisible && <div>{description}</div>}
    </div>
  );
};

export const Questions = () => {
  return (
    <div>
      <div className={s.title}>Frequently Asked Questions</div>
      <p className={s.desc}>
        Here are some of our FAQs. If you have any other questions you'd like
        answered please feel free to email us.
      </p>

      <Question question="What is Bookmark?" description="Opis bookmarks" />
