// Newsletter.js

import React, { useEffect, useState } from "react";
import s from "./newsletter.module.css";

export const Newsletter = () => {
  const [count, setCount] = useState(35000);

  useEffect(() => {
    const countdownElement = document.getElementById("countdown");

    if (countdownElement) {
      countdownElement.innerHTML = count;

      const intervalId = setInterval(() => {
        setCount((prevCount) => prevCount - 1);
        countdownElement.innerHTML = count;
      }, 1000);

      setTimeout(() => {
        clearInterval(intervalId);
        countdownElement.innerHTML = 0;
      }, 20000);
    }
  }, [count]);

  return (
    <div className={s.bg}>
      <div id="countdown"></div>
      <div className={s.stay}>Stay up-to-date with what we're doing</div>
      <div className={s.flex}>
        <input className={s.input} placeholder="Enter your email address" />
        <button className={s.btn}>Contact us</button>
      </div>
    </div>
  );
};
