import React, { useState } from "react";
import s from "./features.module.css";
import img1 from "../../images/svg/illustration-features-tab-1.svg";
import img2 from "../../images/svg/illustration-features-tab-2.svg";
import img3 from "../../images/svg/illustration-features-tab-3.svg";

export const Features = () => {
  const data = [
    {
      title: "Simple Bookmarking",
      desc: "dsdd",
      img: img1,
    },
    {
      title: "Speedy Searching",
      desc: "dsdddsdasdd",
      img: img2,
    },
    {
      title: "Easy Sharing",
      desc: "dsdddsdasddfdfdsff",
      img: img3,
    },
  ];

  const [selectedCategory, setSelectedCategory] = useState(0);

  const handleCategoryClick = (index) => {
    setSelectedCategory(index);
  };

  return (
    <div>
      <h2 className={s.title}>Features</h2>
      <p>
        Our aim is to make it quick and easy for you to access your favourite
        websites. Your bookmarks sync between your devices so you can access
        them on the go.
      </p>
      <div>
        {data.map((el, index) => (
          <div
            key={index}
            className={`${s.category} ${
              selectedCategory === index ? s.selectedCategory : ""
            }`}
            onClick={() => handleCategoryClick(index)}
          >
            {el.title}
          </div>
        ))}
      </div>
      <div>
        {selectedCategory !== null && (
          <div>
            <div>
              <img className={s.photo} src={data[selectedCategory].img}></img>
            </div>
            <div>
              {" "}
              Tutaj renderowane tresci dla kategorii:{" "}
              {data[selectedCategory].title}
            </div>
            <div>{data[selectedCategory].desc} </div>
          </div>
        )}
      </div>
    </div>
  );
};
