import React, { useState, useEffect } from "react";
import s from "./modalDelay.module.scss";
import { Modal } from "./modal";

export const ModalDelay = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const [hasModalBeenClosed, setHasModalBeenClosed] = useState(false);
  // useEffect(() => {
  //   let timerId;

  //   const handleMouseOver = () => {
  //     if (!isModalOpen && !hasModalBeenClosed) {
  //       timerId = setTimeout(() => {
  //         setIsModalOpen(true);
  //       }, 5000);
  //     }
  //   };

  //   window.addEventListener("mouseover", handleMouseOver);

  //   return () => {
  //     window.removeEventListener("mouseover", handleMouseOver);
  //     clearTimeout(timerId);
  //   };
  // }, [isModalOpen, hasModalBeenClosed]);

  useEffect(() => {
    let timerIdMouseOver;
    let timerIdTimeout;

    const handleMouseOver = () => {
      if (!isModalOpen && !hasModalBeenClosed) {
        clearTimeout(timerIdTimeout); // Zatrzymaj timer wywołujący po 20 sekundach
        setIsModalOpen(true);
      }
    };

    const handleTimeout = () => {
      if (!isModalOpen && !hasModalBeenClosed) {
        setIsModalOpen(true);
      }
    };

    window.addEventListener("mouseover", handleMouseOver);

    // Uruchomienie modala po 20 sekundach, nawet jeśli kursor nie znajdzie się nad obszarem strony
    timerIdTimeout = setTimeout(handleTimeout, 30000);

    return () => {
      window.removeEventListener("mouseover", handleMouseOver);
      clearTimeout(timerIdMouseOver);
      clearTimeout(timerIdTimeout);
    };
  }, [isModalOpen]);

  const closeModal = () => {
    setIsModalOpen(false);
    setHasModalBeenClosed(true);
  };

  return (
    <div>
      {isModalOpen && (
        <div className={s.modalOverlay}>
          <div className={s.modalContent}>
            <div>
              <div onClick={closeModal}>XX</div>
              <Modal />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
