import React, { useState, useEffect } from "react";
import s from "./modalDelay.module.css";
export const ModalDelay = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    let timerId;

    // Funkcja obsługująca otwarcie modala po najechaniu kursorem
    const handleMouseOver = () => {
      if (!isModalOpen) {
        timerId = setTimeout(() => {
          setIsModalOpen(true);
        }, 3000);
      }
    };

    // Dodajemy nasłuchiwanie zdarzenia najechania kursorem
    window.addEventListener("mouseover", handleMouseOver);

    // Wyczyszczenie timera i zdarzenia po odmontowaniu komponentu
    return () => {
      window.removeEventListener("mouseover", handleMouseOver);
      clearTimeout(timerId);
    };
  }, [isModalOpen]);

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div>
      {isModalOpen && (
        <div className={s.modalOverlay}>
          <div className={s.modalContent}>
            <p>Tutaj jest zawartość modala</p>
            <button onClick={closeModal}>Zamknij</button>
          </div>
        </div>
      )}
    </div>
  );
};
