import React, { useState, useEffect } from "react";
import s from "./newsletter.module.css";

export const Newsletter = () => {
  const [count, setCount] = useState(35000);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCount((prevCount) =>
        Math.max(0, prevCount - Math.ceil(35000 / 20 / 2))
      ); // Płynne zmniejszanie co pół sekundy
    }, 500);

    setTimeout(() => {
      clearInterval(intervalId);
    }, 20000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <>
      <div className={s.bg}>
        <div>{count}+ Already Joined</div>
        <div className={s.stay}>Stay up-to-date with what we`re doing</div>
        <div className={s.flex}>
          <input
            className={s.input}
            placeholder="Enter your email address"
          ></input>
          <button className={s.btn}> Contact us</button>
        </div>
      </div>
    </>
  );
};
