import React, { useState, useEffect } from "react";
import s from "./modalDelay.module.scss";
import { Modal } from "./modal";

export const ModalDelay = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [hasModalBeenClosed, setHasModalBeenClosed] = useState(false);

  useEffect(() => {
    let timerId;

    const handleMouseMove = (event) => {
      const mouseY = event.clientY;
      if (!isModalOpen && !hasModalBeenClosed && mouseY <= 15) {
        setIsModalOpen(true);
      }
    };

    window.addEventListener("mousemove", handleMouseMove);

    // Uruchomienie modala po 30 sekundach od wejścia na stronę, tylko jeśli modal nie jest jeszcze otwarty
    if (!isModalOpen) {
      timerId = setTimeout(() => {
        setIsModalOpen(true);
      }, 30000);
    }

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      clearTimeout(timerId);
    };
  }, [isModalOpen, hasModalBeenClosed]);

  const closeModal = () => {
    setIsModalOpen(false);
    setHasModalBeenClosed(true);
  };

  return (
    <div>
      {isModalOpen && (
        <div className={s.modalOverlay}>
          <div className={s.modalContent}>
            <div>
              <div onClick={closeModal}>XX</div>
              <Modal />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
