import React, { useState, useEffect } from "react";
import s from "./modalDelay.module.css";

export const ModalDelay = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    const hasModalBeenClosed = localStorage.getItem("hasModalBeenClosed");

    if (!isModalOpen && !hasModalBeenClosed) {
      const timerId = setTimeout(() => {
        setIsModalOpen(true);
      }, 30000);

      return () => {
        clearTimeout(timerId);
      };
    }
  }, [isModalOpen]);

  const closeModal = () => {
    setIsModalOpen(false);
    localStorage.setItem("hasModalBeenClosed", "true");
  };

  return (
    <div>
      {isModalOpen && (
        <div className={s.modalOverlay}>
          <div className={s.modalContent}>
            <p>Tutaj jest zawartość modala</p>
            <button onClick={closeModal}>Zamknij</button>
          </div>
        </div>
      )}
    </div>
  );
};
