import React, { useState, useEffect } from "react";
import s from "./modalDelay.module.css";
export const ModalDelay = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [hasModalBeenClosed, setHasModalBeenClosed] = useState(false);

  useEffect(() => {
    let timerId;

    const handleMouseOver = () => {
      if (!isModalOpen && !hasModalBeenClosed) {
        timerId = setTimeout(() => {
          setIsModalOpen(true);
        }, 30000);
      }
    };

    window.addEventListener("mouseover", handleMouseOver);

    return () => {
      window.removeEventListener("mouseover", handleMouseOver);
      clearTimeout(timerId);
    };
  }, [isModalOpen, hasModalBeenClosed]);

  const closeModal = () => {
    setIsModalOpen(false);
    setHasModalBeenClosed(true);
  };

  return (
    <div>
      {isModalOpen && (
        <div className="modalOverlay">
          <div className="modalContent">
            <p>Tutaj jest zawartość modala</p>
            <button onClick={closeModal}>Zamknij</button>
          </div>
        </div>
      )}
    </div>
  );
};
